import React, { useState, useEffect, Fragment } from 'react';

const ListWords = () => {
	//use state to manage available entries
	const [words, setWords] = useState([]);

	const getWords = async () => {
		try {
			const response = await fetch('http://localhost:5000/words');

			//parse received data
			const wordAndDef = await response.json();

			//set state of words to contain all entries in an array
			setWords(wordAndDef);
		} catch (err) {
			console.error(err.message);
		}
	};

	useEffect(() => {
		getWords();
	}, []);

	//return all words in table
	return (
		<Fragment>
			<table className='table table-striped mt-3'>
				<thead className='table-dark'>
					<tr>
						<th></th>
						<th>Word</th>
						<th>Definition</th>
					</tr>
				</thead>
				<tbody>
					{/* <tr>
						<td>John</td>
						<td>Doe</td>
						<td>john@example.com</td>
					</tr> */}
					{words.map((entry) => {
						return (
							<tr key={entry.word_id}>
								<td></td>
								<td>{entry.word}</td>
								<td>{entry.definition}</td>
							</tr>
						);
					})}
				</tbody>
			</table>
		</Fragment>
	);
};

export default ListWords;
