import React, { Fragment, useState, useEffect } from 'react';

const EditDef = (props) => {
	const [def, setDef] = useState('');
	const id = props.wordId;

	const getDef = async () => {
		try {
			const response = await fetch(`http://localhost:5000/words/${id}`);

			//parse received data
			const wordAndDef = await response.json();

			//set state of words to contain all entries in an array
			setDef(wordAndDef.definition);
		} catch (err) {
			console.error(err.message);
		}
	};

	useEffect(() => {
		getDef();
	}, []);

	return (
		<Fragment>
			<button
				type='button'
				className='btn btn-primary'
				data-toggle='modal'
				data-target='#myModal'
			>
				Edit
			</button>

			<div className='modal' id='myModal'>
				<div className='modal-dialog'>
					<div className='modal-content'>
						<div className='modal-header'>
							<h4 className='modal-title'>Edit definition</h4>
							<button type='button' className='close' data-dismiss='modal'>
								&times;
							</button>
						</div>

						<div className='modal-body'>
							<form>
								<div class='form-group'>
									<label for='exampleFormControlTextarea1'></label>
									<textarea
										class='form-control'
										id='exampleFormControlTextarea1'
										rows='3'
										defaultValue={getDef}
									></textarea>
								</div>
							</form>
						</div>

						<div className='modal-footer'>
							<button
								type='button'
								className='btn btn-success'
								data-dismiss='modal'
							>
								Save Changes
							</button>
							<button
								type='button'
								className='btn btn-danger'
								data-dismiss='modal'
							>
								Close
							</button>
						</div>
					</div>
				</div>
			</div>
		</Fragment>
	);
};

export default EditDef;
